import * as React from 'react';

/**
 * Modal footer properties.
 */
export default interface IModalFooterProps extends React.HTMLAttributes<HTMLElement> {
    /** Property to set if you want the HTML tag to be something else */
    tag?: React.ReactType;

    /** Property to set any CSS classnames on the modal */
    className?: string;

    /** Property to set any CSS classModule on the modal */
    cssModule?: object;
}
