import classNames from 'classnames';
import * as React from 'react';
import { mapToCssModules } from '../common/utilities';
import IModalFooterProps from './ModalFooter.props';

/**
 * ModalFooter component
 */
export default class ModalFooter extends React.Component<IModalFooterProps> {
    public static defaultProps: Partial<IModalFooterProps> = {
        tag: 'div'
    };

    public render(): JSX.Element {
        const { className, cssModule, tag, ...attributes } = this.props;
        const Tag = tag || 'div';
        const classes = mapToCssModules(classNames(className, 'msc-modal__footer'), cssModule);
        return <Tag {...attributes} className={classes} />;
    }
}
