import * as React from 'react';

/**
 * Modal header properties.
 */
export default interface IModalHeaderProps extends React.HTMLAttributes<HTMLElement> {
    /** Property to set if you want the HTML tag to be something else */
    tag?: React.ReactType;

    /** Property to set for the aria label for the close button. */
    closeAriaLabel?: string;

    /** Property to set the close button label text. */
    closeButtonLabel?: string;

     /** ID of the modal header */
     id?:string;

    /** Property to set if you want the wrapping HTML tag to be something else. */
    wrapTag?: React.ReactType;

    /** Property to set any CSS classnames on the modal */
    className?: string;

    /** Property to set any CSS classModule on the modal */
    cssModule?: object;

    /** Callback for toggling the modal. */
    toggle?(): void;
}
