/**
 * Navbar Brand properties.
 */
export interface IPaginationItemProps extends React.HTMLAttributes<HTMLElement> {

    /** Tag property to set if you want the HTML tag to be something else */
    tag?: React.ReactType;

    /** ClassName Property to set any CSS classnames on the pagination item */
    className?: string;

    /** CssModule Property to set any CSS classModule on the pagination item */
    cssModule?: object;

    /** Flag to set if the pagination item is active */
    active?: boolean;

    /** Flag to set if the pagination item is disabled */
    disabled?: boolean;
}
