import { ITelemetryContent } from '../common/telemetryHelper';

/**
 * Navbar Toggler properties.
 */
export interface IPaginationLinkProps extends React.HTMLProps<HTMLAnchorElement> {

    /** Tag property to set if you want the HTML tag to be something else */
    tag?: React.ReactType;

    /** ClassName Property to set any CSS classnames on the navbar toggler */
    className?: string;

    /** CssModule Property to set any CSS classModule on the navbar toggler */
    cssModule?: object;

    /** Flag to set if there is next item */
    next?: boolean;

    /** Flag to set if there is previous item */
    previous?: boolean;

    /** The telemetry content */
    telemetryContent?: ITelemetryContent;
}
