import * as React from 'react';
import * as renderer from 'react-test-renderer';

import Progress from './Progress';

describe('Progress', () => {
    it('renders default correctly', () => {
        expect(renderer.create(<Progress value={0} max={100}/>).toJSON()).toMatchSnapshot();
    });
});

describe('Progress', () => {
    it('renders success correctly', () => {
        expect(renderer.create(<Progress className='progress-bar bg-success' value={100} max={100}/>).toJSON()).toMatchSnapshot();
    });
});

describe('Progress', () => {
    it('renders aria-busy as false at 100%', () => {
        expect(renderer.create(<Progress value={100} max={100}/>).toJSON()).toMatchSnapshot();
    });
});

describe('Progress', () => {
    it('renders simple string aria-label correctly', () => {
        expect(renderer.create(<Progress value={50} ariaLabel='Half way done' max={100}/>).toJSON()).toMatchSnapshot();
    });
});

describe('Progress', () => {
    it('renders {percent} replacement string aria-label correctly', () => {
        expect(renderer.create(<Progress value={50} ariaLabel='{percent}%' max={100}/>).toJSON()).toMatchSnapshot();
    });
});