import { render } from 'enzyme';
import * as React from 'react';
import SingleSlideCarousel from './SingleSlideCarousel';

describe('Single slide carousel', () => {
    const styles = {height: '500px', width: '215px'};
    const vertContainerStyle = {height: '1000px' };
    it('renders the single slide carousel', () => {
        const carousel = render(
            <SingleSlideCarousel>
                <li className='bg-primary mr-2' style={styles} tabIndex={0}/>
                <li className='bg-secondary mr-2' style={styles} tabIndex={0}/>
                <li className='bg-success mr-2' style={styles} tabIndex={0}/>
                <li className='bg-danger mr-2' style={styles} tabIndex={0}/>
                <li className='bg-warning mr-2' style={styles} tabIndex={0}/>
                <li className='bg-info mr-2' style={styles} tabIndex={0}/>
                <li className='bg-light mr-2' style={styles} tabIndex={0}/>
                <li className='bg-dark mr-2' style={styles} tabIndex={0}/>
                <li className='bg-warning mr-2' style={styles} tabIndex={0}/>
            </SingleSlideCarousel>
        );

        expect(carousel).toMatchSnapshot();
    });

    it('renders the vertical single slide carousel', () => {
        const carousel = render(
            <div style={vertContainerStyle}>
                <SingleSlideCarousel vertical={true}>
                    <li className='bg-primary mb-2' style={styles} tabIndex={0}/>
                    <li className='bg-secondary mb-2' style={styles} tabIndex={0}/>
                    <li className='bg-success mb-2' style={styles} tabIndex={0}/>
                    <li className='bg-danger mb-2' style={styles} tabIndex={0}/>
                    <li className='bg-warning mb-2' style={styles} tabIndex={0}/>
                    <li className='bg-info mb-2' style={styles} tabIndex={0}/>
                    <li className='bg-light mr-2' style={styles} tabIndex={0}/>
                    <li className='bg-dark mb-2' style={styles} tabIndex={0}/>
                    <li className='bg-warning mb-2' style={styles} tabIndex={0}/>
                </SingleSlideCarousel>
            </div>
        );

        expect(carousel).toMatchSnapshot();
    });

});
