import classnames from 'classnames';

import * as React from 'react';

export interface IRadioButtonProps {
    isChecked?: boolean;
    value?: string;
    ariaSetSize?: number;
    ariaPosInSet?: number;
    ariaLabel?: string;
    name:string;
    className?:string;
    onChange(reacords:object): void;
}

/**
 *
 * AddPaymentForm SFC
 * @extends {React.FC<IRadioButtonProps>}
 */
const CheckBoxButton: React.FC<IRadioButtonProps> = ({
    isChecked,
    value,
    ariaLabel,
    name,
    className,
    onChange
}) => {

    const cssClass = classnames('ms-input-radio', className ? className : '');
    return (
        <input
            className={cssClass}
            checked={isChecked}
            aria-checked={isChecked}
            type='checkbox'
            aria-label={ariaLabel}
            value={value}
            name={name}
            onChange={onChange}
        />
    );
};

export default CheckBoxButton;