/* tslint:disable:no-unused-variable */
import { mount } from 'enzyme';
import * as React from 'react';
/* tslint:enable:no-unused-variable */
import UncontrolledTooltip from '../tooltip/UncontrolledTooltip';

const testRef: React.RefObject<HTMLAnchorElement> = React.createRef();
describe('UncontrolledTooltip', () => {
    it('UncontrolledTooltip should be open on click', () => {
        const wrapper = mount(
            <div>
                <a href='#tooltip' id='TooltipExample' ref={testRef}>tooltip</a>
                <UncontrolledTooltip target={testRef}>Test content</UncontrolledTooltip>
            </div>,
            { attachTo: document.body.firstElementChild as HTMLElement }
        );
        const clickableLink = document.querySelector('#TooltipExample') as HTMLElement;
        clickableLink.click();
        const tooltipHtml = document.querySelector('.tooltip-inner') as HTMLElement;
        expect(tooltipHtml).toBeDefined();
        wrapper.unmount();
    });
});