import * as Msdyn365 from '@msdyn365-commerce/core';
import * as React from 'react';
import { INodeProps } from './node';

export interface IModuleProps extends INodeProps {
    moduleProps: Msdyn365.IModule;
}

export const Module: React.FC<IModuleProps> = ({ className = '', id = '', tag: Tag = 'div', moduleProps, ...restAttribute }) => (
    <Tag className={className} id={id} {...moduleProps.renderModuleAttributes(moduleProps)} {...restAttribute} />
);

export default Module;
