import { ICache, ICacheItem } from '@msdyn365-commerce/core-internal';
/**
 * SimpleCache implementation
 *
 * @class SimpleCache
 * @implements {ICache}
 * @internal
 */
export default class SimpleCache implements ICache {
    localCache: {
        [type: string]: {
            [key: string]: ICacheItem;
        };
    };
    constructor(useRequestCache: boolean);
    Get<T>(type: string, key: string): ICacheItem<T> | null;
    Put<T>(type: string, key: string, item: T): ICacheItem<T> | null;
    GetValue<T>(type: string, key: string): T | null;
    Value<T>(item: ICacheItem<T>): T;
}
