import { IDictionary } from '@msdyn365-commerce/core-internal';
export interface ICacheSettings {
    /**
     * timeToRefresh cache item in seconds
     */
    ttrInSeconds?: IDictionary<number>;
    /**
     * timeToLive cache item in seconds
     */
    ttlInSeconds?: IDictionary<number>;
    /**
     * Time(in seconds) to check the cache to delete ttl expired cache items
     */
    checkPeriodInSeconds?: number;
    /**
     * Default Time To Live(in seconds) for all the cache items
     */
    defaultTTLInSeconds?: number;
    /**
     * Default Time To Refresh(in seconds) for all the cache items
     */
    defaultTTRInSeconds?: number;
}
