import { Category } from '@msdyn365-commerce/retail-proxy';
/**
 * Category along with children
 */
export interface CategoryHierarchy extends Category {
    /**
     * @description Array of children categories
     */
    Children: CategoryHierarchy[];
    /**
     * @description Url Slug for Category
     */
    Slug?: string;
    /**
     * @description Full url for category
     */
    Url?: string;
    /**
     * @description Neutralized name
     */
    NeutralizedName?: string;
}
