import * as React from 'react';
import { ITransitionProps } from '../fade/Fade.props';

/**
 * CarouselItem props
 */

export interface ICarouselItemProps extends React.HTMLProps<HTMLElement>, ITransitionProps {

    /** HTML tag of the element */
    tag?: React.ReactType;

    /** Property to control ?  */
    in?: boolean;

    /** Property to set any CSS classModule on the button */
    cssModule?: object;

    /** controls whether the slide animation on the Carousel works or not */
    slide?: boolean;

    /** Set the timeout */
    timeout?: number;

    /** Children for the React element */
    children?: React.ReactChild[] | React.ReactChild | React.ReactNode;
}
