import { ITelemetryContent } from '@msdyn365-commerce-modules/utilities';
import { buildMockCoreContext } from '@msdyn365-commerce/core';
import { ICartState } from '@msdyn365-commerce/global-state';
import { render } from 'enzyme';
import * as React from 'react';
import { CartIconComponent, ICartIconComponentProps } from './cart-icon.component';

describe('Cart icon tests', () => {
    it('renders correctly', () => {
        const mockProps: ICartIconComponentProps = {
            context: buildMockCoreContext({}),
            data: {cart: {totalItemsInCart: 3} as ICartState},
            id: 'id',
            typeName: 'typeName',
            cartLabel: 'Shopping bag, {0} items',
            telemetryContent:{} as ITelemetryContent,
        };
        const component = render(<CartIconComponent {...mockProps}/>);
        expect(component).toMatchSnapshot();
    });
});