/* tslint:disable:no-unused-variable */
import { render } from 'enzyme';
import * as React from 'react';
import Fade from './Fade';

describe('Fade', () => {
    it('renders basic Primary Fade correctly', () => {
        const fade = render(
            <div>
                <button color='primary'>Toggle Fade</button>
                <Fade tag='h5' className='mt-3'>
                    This content will fade in and out as the button is pressed
                </Fade>
            </div>
        );
        expect(fade).toMatchSnapshot();
    });

    it('renders basic Primary Fade correctly with added timeout prop', () => {
        const fade = render(
            <div>
                <button color='primary'>Toggle Fade</button>
                <Fade tag='h5' timeout={50} className='mt-3'>
                    This content will fade in and out as the button is pressed
                </Fade>
            </div>
        );
        expect(fade).toMatchSnapshot();
    });
});
