/* tslint:disable:no-unused-variable */
import { mount } from 'enzyme';
import * as React from 'react';
import Fade from './Fade';
import { FadeExample } from './Fade.examples';

describe('Fade', () => {
    it('renders basic Primary Fade correctly', () => {
        const onClick = jest.fn();
        const wrapper = mount(
            <FadeExample>
                <button onClick={onClick}>Toggle Fade</button>
                <Fade tag='h5' className='mt-3'>
                    This content will fade in and out as the button is pressed
                </Fade>
            </FadeExample>,
            { attachTo: document.body.firstElementChild as HTMLElement }
        );
        const fade = wrapper.instance() as FadeExample;
        expect(fade.state.fadeIn).toBe(false);
        wrapper.find('button').simulate('click');
        expect(fade.state.fadeIn).toBe(true);
        setTimeout(() => { expect(wrapper.find('h5').getDOMNode().getAttribute('class')).toBe('mt-3 fade show');}, 100);
    });
});
