import { buildMockCoreContext, IImageData } from '@msdyn365-commerce/core';
import { render } from 'enzyme';
import * as React from 'react';
import { ISocialMediaProps, SocialMedia, SocialMediaComponent } from './social-media.components';

describe('Cart icon tests', () => {
    it('renders correctly', () => {
        const imageData: IImageData ={
            src:'https://cms-ppe-imageresizer-mr.trafficmanager.net/cms/api/gfhwnkhdlh/imageFileData/MC3EAQ'
        };
        const mockProps: ISocialMediaProps = {
            context: buildMockCoreContext({}),
            data: {},
            id: 'id',
            typeName: 'typeName',
            postUrl: 'https://www.microsoft.com/en-us/',
            socialMedia: SocialMedia.Facebook,
            src: imageData
        };
        const component = render(<SocialMediaComponent {...mockProps}/>);
        expect(component).toMatchSnapshot();
    });
});