import ActionToggle from './ActionToggle';

export interface IActionToggleChangeNotification {
    /** The ActionToggle id */
    id?: string;
    /** The ActionToggle */
    actionToggle: ActionToggle;
    /** The toggled state */
    toggled: boolean;
    /** The event object */
    event: React.KeyboardEvent;
}

export interface IActionToggleProps {
    /** Sets the id */
    id?: string;
    /** Sets the css classes */
    className?: string;
    /** Sets the toggled state */
    toggled?: boolean;
    /** Sets the aria-hidden state to hide/show the action-toggle */
    'aria-hidden'?: boolean;
    /** Sets the changed notification callback */
    onChange?(notfication: Readonly<IActionToggleChangeNotification>): void;
}
