import { render } from 'enzyme';
import * as React from 'react';

import ActionToggle from './ActionToggle';

describe('ActionToggle', () => {
    it('does not render with less then two children', () => {
        const actionToggle = render(
            <ActionToggle id='one-child'>
                <button
                    type='button'
                    className='btn btn-secondary btn-lg rounded-circle border-light'
                >
                    <i className='fa fa-play' />
                </button>
            </ActionToggle>
        );
        expect(actionToggle).toMatchSnapshot();
    });
});

describe('ActionToggle', () => {
    it('renders untoggled correctly', () => {
        const actionToggle = render(
            <ActionToggle id='untoggled'>
                <button
                    type='button'
                    className='btn btn-secondary btn-lg rounded-circle border-light'
                >
                    <i className='fa fa-play' />
                </button>
                <button
                    type='button'
                    className='btn btn-secondary btn-lg rounded-circle border-light'
                >
                    <i className='fa fa-pause' />
                </button>
            </ActionToggle>
        );
        expect(actionToggle).toMatchSnapshot();
    });
});

describe('ActionToggle', () => {
    it('renders toggled correctly', () => {
        const actionToggle = render(
            <ActionToggle id='toggled' toggled>
                <button
                    type='button'
                    className='btn btn-secondary btn-lg rounded-circle border-light'
                >
                    <i className='fa fa-play' />
                </button>
                <button
                    type='button'
                    className='btn btn-secondary btn-lg rounded-circle border-light'
                >
                    <i className='fa fa-pause' />
                </button>
            </ActionToggle>
        );
        expect(actionToggle).toMatchSnapshot();
    });
});

describe('ActionToggle', () => {
    it('renders aria-hidden correctly', () => {
        const actionToggle = render(
            <ActionToggle id='hidden' aria-hidden={true}>
                <button
                    type='button'
                    className='btn btn-secondary btn-lg rounded-circle border-light'
                >
                    <i className='fa fa-play' />
                </button>
                <button
                    type='button'
                    className='btn btn-secondary btn-lg rounded-circle border-light'
                >
                    <i className='fa fa-pause' />
                </button>
            </ActionToggle>
        );
        expect(actionToggle).toMatchSnapshot();
    });
});