import { render } from 'enzyme';
import * as React from 'react';

import { UncontrolledActionToggle } from './UncontrolledActionToggle';

describe('Example: UncontrolledActionToggle Play / Pause', () => {
    it('renders', () => {
        const actionToggle = render(
            <UncontrolledActionToggle id='my-uncontrolled-action-toggle-glyphOnly'>
                <button
                    type='button'
                    className='btn btn-secondary btn-lg rounded-circle border-light'
                >
                    <i className='fa fa-play' />
                </button>
                <button
                    type='button'
                    className='btn btn-secondary btn-lg rounded-circle border-light'
                >
                    <i className='fa fa-pause' />
                </button>
            </UncontrolledActionToggle>
        );
        expect(actionToggle).toMatchSnapshot();
    });
});

describe('Example: Expand/collapse text', () => {
    it('renders', () => {
        const actionToggle = render(
            <UncontrolledActionToggle id='my-uncontrolled-action-toggle-button'>
                <button
                    type='button'
                    className='btn btn-secondary btn-lg rounded-circle border-light'
                >
                    <i className='fa fa-play' />
                </button>
                <button
                    type='button'
                    className='btn btn-secondary btn-lg rounded-circle border-light'
                >
                    <i className='fa fa-pause' />
                </button>
            </UncontrolledActionToggle>
        );
        expect(actionToggle).toMatchSnapshot();
    });
});