import { render } from 'enzyme';
import * as React from 'react';
import AgeGate from './AgeGate';

describe('AgeGate', () => {
    it('renders ageGate correctly without Submit function', () => {
        const ageGate = render(
            <AgeGate
                aria-hidden={false}
                minmumAge={18}
            />

        );
        expect(ageGate).toMatchSnapshot();
    });
});

describe('AgeGate', () => {
    it('renders ageGate correctly if aria-hidden is true', () => {
        const ageGate = render(
            <AgeGate
                aria-hidden={true}
                minmumAge={18}
            />

        );
        expect(ageGate).toMatchSnapshot();
    });
});

describe('AgeGate', () => {
    it('renders ageGate correctly if minimum age is not specified', () => {
        const spySubmit = jest.fn();
        const ageGate = render(
            <AgeGate
                onSubmitClick={spySubmit}
                aria-hidden={false}
            />

        );
        expect(ageGate).toMatchSnapshot();
    });
});

describe('AgeGate', () => {
    it('renders ageGate correctly with all the properties', () => {
        const spySubmit = jest.fn();
        const ageGate = render(
            <AgeGate
                onSubmitClick={spySubmit}
                aria-hidden={false}
                minmumAge={18}
            />

        );
        expect(ageGate).toMatchSnapshot();
    });
});