/**
 * @interface IAudioTrack
 * @description - The data contract interface to extract information about audio tracks in the video.
 */
export interface IAudioTrack {

    /**
     * @name isDescriptiveAudio
     * @description flag incating if track represents a descriptive audio track
     */
    isDescriptiveAudio: boolean;

    /**
     * @name languageCode
     * @description language code, usually 3-letter code like ENG for English,
     * but standard locale stirngs like EN-US could also be encountered for some videos.
     * There is no processing done on the code, so it is not in any particular canonical form.
     */
    languageCode: string;

    /**
     * @name bitrate
     * @description bitrate, in bps
     */
    bitrate: number;

    /**
     * @name name
     * @description track name, ex.: AAC_und_ch2_128kbps
     */
    name: string;

    /**
     * @name title
     * @description audio track url
     */
    url: string;
}