import { render } from 'enzyme';
import * as React from 'react';
import { IPlayerData} from '../../components/data/player-data';
import { MediaQuality, MediaTypes } from '../../components/data/player-metadata';
import { IPlayerResources  } from '../../components/data/player-resources';
import Player from './Player';

describe('Player', () => {
    it('renders player correctly', () => {
        const videoPlayerData: IPlayerData = {
            options: {
                autoplay: true,
                mute: true,
                loop: false,
                playPauseTrigger: true,
                lazyLoad: false,
                controls: false
            },
            metaData: {
                videoFiles: [
                    {
                        // tslint:disable-next-line:max-line-length no-inferrable-types no-http-string
                        url: 'https://prod-video-cms-amp-microsoft-com.akamaized.net/tenant/amp/entityid/BBwlT1L?blobrefkey=102&$blob=1',
                        mediaType: MediaTypes.MP4,
                        quality: MediaQuality.HQ
                    }
                ]
            }
        };

        const videoPlayerRes: IPlayerResources = {
            playAriaLabel:'play',
            pauseAriaLabel:'pause',
            closeAriaLabel: 'close',
            muteAriaLabel: 'mute',
            unMuteAriaLabel: 'unmute',
            fullScreenAriaLabel: 'full screen',
            exitFullScreenAriaLabel: 'exit full screen',
            seekBarLabel: 'Seek bar',
            closedCaptionLabel: 'Closed caption'
        };

        const player = render(
            <Player
                playerId='videoTestID'
                playerData={videoPlayerData}
                playerResources={videoPlayerRes}
            />
        );
        expect(player).toMatchSnapshot();
    });
});

