/**
 * Dictionary containing all available URL Tokens for a page
 * Not all tokens will exist on every page, only those that can be parsed
 * based on the structure of the URL will exist
 */
export interface IUrlTokens {
    /**
     * @deprecated
     * The Item ID of the current product
     */
    itemId?: string;
    /**
     * The record ID of the current page type
     */
    recordId?: string;
    /**
     * The friendly name of the current product
     */
    productName?: string;
    /**
     * The current lang-locale of the page
     */
    locale?: string;
    /**
     * The categories
     */
    categories?: string[];
    /**
     * The type of the page, if applicable.
     * Currently Suppported page types: Product, Category, List
     */
    pageType?: string;
}
