import { CacheType } from './ICache';
/**
 * Interface which specifies the caching information for a given action
 */
export interface IActionInput {
    runOn?: 'server' | 'client' | 'serverOnly';
    /**
     * Returns the Action ID that the ActionInput is bound to.
     */
    getActionId?(): string;
    /**
     * Returns the type which is used for caching, could be the same as the response entity
     */
    getCacheObjectType(): string;
    /**
     * Returns the key which is used for caching, usually the ID
     */
    getCacheKey(): string;
    /**
     * Is the return type cache-able
     * @deprecated
     */
    shouldCacheOutput?(): boolean;
    /**
     * Caching strategy
     *  application: caches the output in the application cache spanning multiple request
     *  request: caches the output to the current request
     *  none: does not cache output
     */
    dataCacheType?(): CacheType;
}
