import { BypassCache } from './ICache';
import { IQueryResultSettings } from './IQueryResultSettings';

export interface IDictionary<T> {
    [key: string]: T;
}

export interface ICallerContext {
    requestContext: {
        apiSettings: {
            baseUrl: string;
            channelId: number;
            catalogId: number;
            oun: string;
            baseImageUrl: string;
        };
        user: {
            token: string;
            isAuthenticated: boolean;
        };
        locale: string;
        operationId: string;
        query?: IDictionary<string>;
        accessToken?: string;
    };
}

export interface IContext {
    callerContext: ICallerContext;
    queryResultSettings?: IQueryResultSettings;
    bypassCache?: BypassCache;
}
