import { IDataServiceOptions } from './IDataServiceRequest';
import { IQueryResultSettings } from './IQueryResultSettings';

/**
 * Represents collection of OData action or function parameters.
 */
export interface IODataOperationParameters {
    parameters: unknown;
}

/**
 * Represents a data service query object for internal paramater from DataServiceQuery to DataServiceRequest.
 */
export interface IDataServiceQueryInternal {
    entitySet: string;
    entityType: string;
    key: any;
    action?: string;
    isAction?: boolean;
    data?: IODataOperationParameters;
    returnType: any;
    isReturnTypeACollection?: boolean;
    resultSettings?: IQueryResultSettings;
    filterSettings?: string;
    expands?: string[];
    inlineCount?: boolean;
    options: IDataServiceOptions;
    tokens: { [tokenName: string]: string };
}
