import { generateImageUrl } from '../index';

describe('generateImageUrl', () => {
    it('Gets product image path', async () => {
        const apiSettings = {
            channelId: '5',
            baseImageUrl: 'https://baseurl.com/images/',
            baseUrl: 'https://baseurl.com',
            oun: '069'
        };

        const fullUrl = generateImageUrl('https://my-cool-url.co.uk', apiSettings);
        expect(fullUrl).toBe('https://my-cool-url.co.uk');

        const relativeImageUrl = generateImageUrl('image ^ 1 ^ blue ^ 03.jpg', apiSettings);
        expect(relativeImageUrl).toBe('https://baseurl.com/images/image%20%5E%201%20%5E%20blue%20%5E%2003.jpg');
    });
});
