/**
 * Interface for which methods need to be implemented for an event logger.
 */
export interface IExpLogger {
    /**
     * Performs any client-side initialization and setup for the event logger
     * @param config The config that is required to initialize the logger client-side
     * @param userId The current user being tracked
     */
    initializeClientSide?(config?: any, userId?: string): void;
    /**
     * Logs the given event and payload
     * @param eventName Name of event to log
     * @param payload Extra parameters dependent on the eventType
     */
    logEvent(eventName: string, payload: any): void;
}
