/***
 * Defines the telemetry properties to track for a Page View event
 * @property title     {string}   - Page title
 * @property location  {string}   - Page location
 * @property page      {string}   - Page info
 */
export interface IPageViewInfo {
    title: string;
    location?: string;
    page?: string;
}
